import React, { useState } from 'react'
import axios from '../api/index'

export default function Complain() {
  const [formData,setFormData]=useState({
    type:'',
    uploadfile:'',
    message:'',
    advice:''

  })

  const submithandller=async()=>{
    const{type,uploadfile,message,advice}=formData;
    try{
      const response=await axios.post('complainregister',{type,uploadfile,message,advice});
      console.log(response.data)
    }
    catch(error)
    {
      console.log(error);
    }


  }

  const onChange=(e)=>{
    const{name,value}=e.target;
    setFormData({
      ...formData,
      [name]:value
    })


  }
  return (
   <div>
    <div class="mt-10">
        <h2 class="text-2xl font-bold text-gray-800 text-center">Complain on related field or Types</h2>

        <div class="mt-2 bg-gray-100 shadow-lg  rounded-xl p-5 max-w-md mx-auto">
          <div>
            <form action="" onSubmit={submithandller}>
              <div>
                <div>
                  <select name="type" onChange={changeHandler} value={formD} class="complaint-select block w-full  rounded-lg border border-gray-300 bg-white px-6 py-2 font-semibold text-blue-800 outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-300">
                    <option value="">Please select complaint type</option>
                    <option value="Politics">Politics</option>
                    <option value="Public">Public</option>
                    <option value="Personal">Personal</option>
                  </select>
                </div>

                <div class="  max-w-md rounded-lg bg-white p-6 shadow-lg mt-4 mb-2">
                  <label for="uploadfile" class="mb-2 block text-lg font-semibold text-gray-700">Upload (Image/Video):</label>
                  <input type="file" name="uploadfile" id="uploadfile" class="w-full rounded-md border border-gray-300 px-4 py-3 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500" />
                </div>

                <div class="max-w-md rounded mb-2 ">
                  <div class="text-lg font-bold text-gray-700 ">Message</div>
                  <textarea name="message" class="w-full text-gray-500 font-semibold rounded p-4 outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500 "  id="" cols="30" rows="5"></textarea>
                </div>

                <div class="max-w-md rounded mb-2" > 
                  <div class="text-lg font-bold text-gray-700 ">Advice for solution</div>
                  <textarea  name="advice" class="w-full rounded p-4 text-gray-500 font-semibold outline-none focus:border-blue-500 focus:ring-2 focus:ring-indigo-500"  id="" cols="30" rows="3"></textarea>
                </div>

                <div class="max-w-md">
                  <button type="submit" class="bg-blue-600 text-gray-100 font-bold w-full p-1 rounded cursor-pointer">Complain Now</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>

   </div>
  )
}
