import { createSlice } from "@reduxjs/toolkit";

export const demoSlice = createSlice({
  name: 'demo',
  initialState: {
    cats: [],
    isloading: false,
    error: null,  // Add error property to track any errors
  },
  reducers: {
    getcatsFetch: (state) => {
      state.isloading = true;
      state.error = null;  // Clear previous errors when a new fetch begins
    },
    getcatsSuccess: (state, action) => {
      state.cats = action.payload;
      state.isloading = false;
    },
    getcatsFailure: (state, action) => {
      state.isloading = false;
      state.error = action.payload;  // Store the error message in state
    },
  },
});

export const { getcatsFetch, getcatsSuccess, getcatsFailure } = demoSlice.actions;
export default demoSlice.reducer;
