import React, { useState } from 'react'
import axios from '../api/index'
import { Link } from 'react-router-dom'

export default function Login() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    })




    const changehandel = (e) => {

        const { name, value } = e.target;
        setFormData(
            {
                ...formData,
                [name]: value
            }
        )
    }

    const formhandler = async (e) => {
        e.preventDefault()
        const { username, password } = formData;
        console.log(username, password)

        const response = await axios.post('/login', { username, password });
        if (response) {
            console.log("login successfully");
            console.log(response.data);
            localStorage.setItem("token",response.data.token)
        }
        else {
            console.log("unable to login");
        }

    }


    return (
        <>

            <div class="flex justify-center h-screen shadow-lg   items-center">
                <div class="bg-gray-50 p-4 rounded-xl w-3/12 ">
                    <h2 class="font-bold text-3xl pt-4 mb-2 text-blue-700 text-center">Log In</h2>
                    <p class="font-semibold text-gray-700 text-center ">Login with credintials data</p>
                    <div class="mt-10">
                        <form action="" onSubmit={formhandler}>
                            <div class=" mx-2 p-2 rounded-xl mb-1 ">

                                <input class="bg-gray-100 w-full p-2 rounded-lg shadow-sm focus:bg-border-500 focus:ring-2 focus:ring-indigo-400 outline-none text-gray-800 "
                                    type="text" name="username"
                                    placeholder="Email/Username"
                                    onChange={changehandel}
                                    value={formData.username}
                                />
                            </div>

                            <div class=" mx-2 p-2 rounded-xl ">

                                <input class="bg-gray-100 w-full p-2 rounded-lg shadow-sm ocus:bg-border-500 focus:ring-2 focus:ring-indigo-400 outline-none " onChange={changehandel} value={formData.password} type="password" name="password" placeholder="Password" />
                            </div>
                            <p class=" text-sm font-semibold text-end mx-2 text-blue-600 mb-2"> forgot password?</p>



                            <div class="mx-2">
                                <button type="submit" class="bg-blue-600 text-gray-100 font-bold w-full p-1 rounded-lg ">Login</button>
                            </div>

                        </form>
                        <div class="flex mx-2 mt-1">
                            <p class="text-gray-800 text-sm font-semibold"> Click for Register?</p><a class="text-sm font-semibold mx-1 text-blue-500 " href=""> Signup</a>
                        </div>

                    </div>
                </div>
            </div>


        </>
    )
}
