import createSagaMiddleware from 'redux-saga';
import { configureStore } from '@reduxjs/toolkit';
import catReducer from './DemoSlice';
import catsaga from './Demosaga';

const sagaMiddleware = createSagaMiddleware();

const store = configureStore({
  reducer: {
    cats: catReducer,
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware().concat(sagaMiddleware), // Add the saga middleware to the default ones
});

sagaMiddleware.run(catsaga); // Run the saga

export default store;
