import { call, put, takeEvery } from 'redux-saga/effects';
import axios from '../api/index';  // Ensure this axios instance is properly set up
import { getcomplainSuccess, getcomplainError } from './Complain';

// Worker Saga to handle the async API call
function* working(action) {
  try {
    // Call the API using axios
    const response = yield call(axios.get, '/fetchcomplainuser'); 
    // Dispatch the success action with the response data
    yield put(getcomplainSuccess(response.data));
  } catch (error) {
    // Dispatch the error action if the API call fails
    yield put(getcomplainError(error.message));
  }
}

// Watcher Saga that listens for the 'complain/getcomplainFetch' action
function* complainsaga() {
  yield takeEvery('complain/getcomplainFetch', working);  // Listen for the 'getcomplainFetch' action
}

export default complainsaga;
